mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT SI.SIB_NO ,SI.USER_ID ,P.NM AS NM ,SI.SIB_TTL ,SI.SIB_CN ,SI.SIB_DT ,CASE SI.SIB_STTS_YN WHEN 'N' THEN '요청' WHEN 'Y' THEN '완료' ELSE '알 수 없음' END AS SIB_STTS_YN ,SI.SIB_TIME FROM SYSTEM_INQUIRY_BOARD SI INNER JOIN PERSON P ON SI.USER_ID = P.ID
ORDER BY SI.SIB_NO DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

