mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT SA.SHAP_DOC_NO, SA.SEMSTR_NO, SA.STU_ID, SA.PROFE_ID, SA.SHAP_RECOMMEND, P.NM AS STU_NM, SA.AWARD_CD, A.AWARD_TYPE, A.AWARD_NM AS AWARD_NM, SA.SHAP_RCPT_DATE, SA.SHAP_CHC_DATE, C.COCO_STTS AS COCO_STTS, SA.SHAP_NO_REASON, SA.ATCH_FILE_ID FROM SCHOLARSHIP_APPLICATION SA INNER JOIN PERSON P ON (SA.STU_ID = P.ID) INNER JOIN AWARD A ON (SA.AWARD_CD = A.AWARD_CD) INNER JOIN COMMON_CODE C ON(SA.SHAP_ACC_CD = C.COCO_CD)
ORDER BY SA.SHAP_DOC_NO DESC ) A )B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

