mapper.insert:
INSERT INTO EVENT_REGISTRANT (
     JOB_NO
    ,STU_ID
) VALUES (
     #{jobNo, jdbcType=VARCHAR}     
    ,#{stuId, jdbcType=VARCHAR}     
)

mapper.select:
SELECT COUNT(*)
FROM EVENT_REGISTRANT
WHERE JOB_NO = #{jobNo}
  AND STU_ID = #{stuId}

mapper.delete:
DELETE FROM EVENT_REGISTRANT
WHERE STU_ID = #{stuId, jdbcType=VARCHAR}
AND JOB_NO = #{jobNo, jdbcType=VARCHAR}
