mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT S.STU_ID, S.STU_CARD_DATE, C.COCO_STTS AS COCO_STTS, P.NM AS STU_NM, D.DEPT_NM AS DEPT_NM FROM STUDENTCARD S INNER JOIN COMMON_CODE C ON(S.COCO_CD = C.COCO_CD) INNER JOIN PERSON P ON (S.STU_ID = P.ID) INNER JOIN STUDENT ST ON (S.STU_ID = ST.STU_ID) INNER JOIN DEPARTMENT D ON (ST.DEPT_CD = D.DEPT_NO)
ORDER BY CASE WHEN C.COCO_STTS = '요청' THEN 1 WHEN C.COCO_STTS = '발급' THEN 2 WHEN C.COCO_STTS = '완료' THEN 3 ELSE 4 END, S.STU_ID DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE STUDENTCARD
SET COCO_CD = #{nextStatus}
WHERE STU_ID = #{stuId}

mapper.select:
SELECT COUNT(*)
FROM STUDENTCARD
WHERE STU_ID =#{stuId}

mapper.insert:
INSERT INTO STUDENTCARD (
    STU_ID,
    STU_CARD_DATE,
    COCO_CD
) VALUES (
   #{stuId}
	,SYSDATE
	,'ST01'
)
